const openModal=document.getElementById("open"),openModalNav=document.getElementById("openNav"),modalContainer=document.getElementById("modal_container"),burger=document.querySelector(".burger"),nav=document.querySelector(".nav");openModal.addEventListener("click",(()=>{burger.classList.toggle("active"),modalContainer.classList.add("show"),document.body.classList.add("modal-open")})),openModalNav.addEventListener("click",(()=>{nav.classList.toggle("open"),modalContainer.classList.add("show"),document.body.classList.add("modal-open")})),modalContainer.addEventListener("click",(e=>{e.target===modalContainer&&document.documentElement.clientWidth>=1240&&(modalContainer.classList.remove("show"),document.body.classList.remove("modal-open"))})),burger.addEventListener("click",(function(){burger.classList.contains("active")&&modalContainer.classList.contains("show")?(this.classList.toggle("active"),modalContainer.classList.remove("show"),document.body.classList.remove("modal-open")):(this.classList.toggle("active"),nav.classList.toggle("open"),document.documentElement.clientWidth>=768&&document.body.classList.toggle("modal-open"))}));